CONNECT TO SAMPLE;
SELECT CSE532.FACILITY.FACILITYNAME, CSE532.FACILITY.ADDRESS1, CSE532.FACILITY.COUNTY, CSE532.FACILITY.CITY, CSE532.FACILITY.STATE, CSE532.FACILITY.ZIPCODE, db2gse.st_distance(GEOLOCATION,db2gse.st_point( -72.993983,40.824369,1), 'STATUTE MILE') AS DISTANCE_IN_MILES FROM CSE532.FACILITYCERTIFICATION INNER JOIN CSE532.FACILITY ON CSE532.FACILITYCERTIFICATION.FACILITYID = CSE532.FACILITY.FACILITYID WHERE ATTRIBUTEVALUE = 'Emergency Department' AND db2gse.st_within(GEOLOCATION,db2gse.st_buffer(db2gse.st_point(-72.993983,40.824369,1),10,'STATUTE MILE')) = 1 ORDER BY DISTANCE_IN_MILES LIMIT 1;